
 
$(document).on('click', 'a.js-link', scrollToLink);
$(document).on('click', 'a.js-link-top', scrollToLink);

function scrollToLink() {
    $("html, body").animate({
        scrollTop: $($(this).attr("href")).offset().top - 40 + "px"
    }, {
        duration: 400,
        easing: "swing"
        });
    return false;
}
